import React from "react";
import { View, Image, FlatList } from "react-native";
import CardBook from "../CardBook/CardBook";
import { ItemView, Title, Container } from "../BooksCarousel/styles";
import { PriceMajor, PriceMinor } from "../CardBook/styles";

const values = [
  {
    id:'1',
    imageUrl: "data:image/jpeg;base64,",
    title: "A Travessia",
    oldPrice: "R$ 34.99",
    newPrice: "R$ 30.00",
    marginLeft: '16px'
  },
  {
    id:'2',
    imageUrl: "data:image/jpeg;base64,",
    title: "Jantar Secreto",
    oldPrice: "R$ 48.99",
    newPrice: "R$ 45.00",
  },
  {
    id:'3',
    imageUrl: "data:image/jpeg;base64,",
    title: "Trono de Vidro: A Herdeira do Fogo",
    oldPrice: "R$ 38.78",
    newPrice: "R$ 30.00",
  },
  
];

type ItemProps = { imageUrl: string, title: string, oldPrice: string, newPrice: string, marginLeft: string };

const Item = ({ imageUrl,oldPrice,newPrice,title,marginLeft }: ItemProps) =>{
    <ItemView marginLeft={marginLeft ? marginLeft : "0px"}>
        <Image source={{ uri: imageUrl}}/>
        <Title>{title}</Title>
        <PriceMinor>{newPrice}</PriceMinor>
        <PriceMajor>{oldPrice}</PriceMajor>
    </ItemView>
}

const BooksCarousel = () => {
  return (
  <Container>
    <FlatList
        data={values}
        renderItem={({item}) => <CardBook title={item.title} imgURL={item.imageUrl} oldPrice={item.oldPrice} price={item.newPrice} />}
        keyExtractor={item => item.id}
        horizontal={true}
      />
  </Container>);
};

export default BooksCarousel;
